<?php

namespace App\Http\Controllers;

use App\Models\Clocking;
use Carbon\Carbon;
use Illuminate\Http\Request;
use Illuminate\Http\JsonResponse;
use Illuminate\Support\Facades\Auth;
use Dompdf\Dompdf;
use Illuminate\Support\Facades\DB;

use DateTime;
use DateTimeZone;
use IntlDateFormatter;

class ClockingController extends Controller
{

    public function getClocksActualDay(): JsonResponse
    {
        $user = Auth::user();
        $currentDate = Carbon::now()->format('Y-m-d');
        $clocks = Clocking::where('user_id', $user['id'])
            ->whereDate('date', '=', $currentDate)
            ->get();

        return new JsonResponse([
            'data' => $clocks,
            'username' => $user['name']
        ]);
    }

    public function getClocksByUser(Request $request): JsonResponse
    {
        $dataRequest = $request->all();
        $user = Auth::user();
        $clocks = Clocking::where('user_id', $user['id']);

        if ($dataRequest['startDate'] && $dataRequest['endDate']) {
            $startDate =  Carbon::createFromFormat('d/m/Y', $dataRequest['startDate'])->format('Y-m-d');
            $endDate =  Carbon::createFromFormat('d/m/Y', $dataRequest['endDate'])->format('Y-m-d');
            $clocks = $clocks->whereBetween('date', [$startDate, $endDate]);
        }

        $clocks = $clocks->orderBy('date', 'DESC')
            ->get()
            ->groupBy(function ($d) {
                return Carbon::parse($d->date)->format('Y-m-d');
            });

        $result = [];
        $cont = 0;
        //Format data
        setlocale(LC_TIME, 'es_ES', 'Spanish_Spain', 'Spanish');
        foreach ($clocks as $key => $day) {
            $auxDate = DateTime::createFromFormat("Y-m-d", $key);
            $result[$cont]['day'] = IntlDateFormatter::formatObject(
                $auxDate,
                'EEEE dd/MM/YYYY',
                'es'
            );
            $result[$cont]['total'] = 0;
            foreach ($day as $dates) {
                $result[$cont]['dates'][] = array(
                    'day' => $dates['date'],
                    'type' => $dates['type']
                );
            }
            $cont++;
        }
        //Calculate hours
        $now = new DateTime('now', new DateTimeZone('GMT+1'));
        $now = $now->format('Y-m-d H:i:s');
        foreach ($result as $key => $r) {
            $total = 0;
            for ($i = 0; $i < count($r['dates']); $i += 2) {
                $input = isset($r['dates'][$i]) ? $r['dates'][$i]['day'] : null;
                $output = isset($r['dates'][$i + 1]) ? $r['dates'][$i + 1]['day'] : null;

                if ($input && $output) {
                    $auxCalc = strtotime($output) - strtotime($input);
                    $total += $auxCalc;
                } else if ($input && (substr($input, 0, 10) == substr($now, 0, 10))) {
                    $total += strtotime($now) - strtotime($input);
                }
            }
            $t = round(abs($total));
            $result[$key]['total'] = sprintf('%02d:%02d:%02d', ($t / 3600), ($t / 60 % 60), $t % 60);;
        }

        return new JsonResponse([
            'data' => $result,
            'user' => $user
        ]);
    }

    public function store(Request $request)
    {
        $user = Auth::user();
        $currentDate = Carbon::now()->setTimezone('Europe/London');
        $type = $request->type;

        $newClock = new Clocking();
        $newClock->user_id = $user['id'];
        $newClock->date = $currentDate;
        $newClock->type = $type;
        $newClock->save();
    }

    public function listHistoryClocks(Request $request)
    {
        $filters = $request->all();
        $historyClocks = $this->getHistoryClocks($filters);
        return new JsonResponse([
            'success' => true,
            'data' => $historyClocks
        ]);
    }

    public function getHistoryClocks($filters = null)
    {
        setlocale(LC_TIME, 'es_ES', 'Spanish_Spain', 'Spanish');
        $clocks = DB::table('clockings')
            ->select('clockings.*', DB::raw('CONCAT(users.name, " ", users.firstname, " ", users.secondname) AS user_fullname'))
            ->join('users', 'clockings.user_id', '=', 'users.id');


        if (isset($filters['user'])) {
            $clocks = $clocks->where('clockings.user_id', $filters['user']);
        }

        if (isset($filters['startDate']) && !empty($filters['startDate'])) {

            $startDate =  Carbon::createFromFormat('d/m/Y', $filters['startDate']);
            $clocks = $clocks->whereDate('clockings.date', '>=', $startDate);
        }

        if (isset($filters['endDate']) && !empty($filters['endDate'])) {
            $endDate =  Carbon::createFromFormat('d/m/Y', $filters['endDate']);
            $clocks = $clocks->whereDate('clockings.date', '<=', $endDate);
        }

        $clocks = $clocks
            ->orderBy('clockings.date', 'asc')
            ->get();

        $result = [];
        $cont = 1;
        $position = 0;
        foreach ($clocks as $clock) {
            $dateExploded = explode(' ', $clock->date);
            $dateExplodedBefore = explode(' ', $clocks[$position]->date);
            $dateExplodedFormated = Carbon::createFromFormat('Y-m-d', $dateExploded[0])->format('d/m/Y');

            if ($cont % 2 === 0) {
                $sizeHourArrayByDate = count($result[$clock->user_fullname][$dateExplodedFormated]['hour']);
                $result[$clock->user_fullname][$dateExplodedFormated]['hour'][$sizeHourArrayByDate - 1]['S'] = $clock->date;
                $fechaInicio = new DateTime($result[$clock->user_fullname][$dateExplodedFormated]['hour'][$sizeHourArrayByDate - 1]['E']);
                $fechaFin = new DateTime($clock->date);
                $intervalo = $fechaFin->diff($fechaInicio);
                $result[$clock->user_fullname][$dateExplodedFormated]['hour'][$sizeHourArrayByDate - 1]['TOTAL'] = $intervalo->h . 'h:' . $intervalo->i . 'm:' . $intervalo->s . 's';
                $cont = 0;
            } else {
                if ($dateExplodedBefore === $dateExploded && $position !== 0) {
                    $result[$clock->user_fullname][$dateExplodedFormated]['hour'][] = ['E' => $clock->date];
                } else {
                    $result[$clock->user_fullname][$dateExplodedFormated] = ['hour' => [['E' => $clock->date]]];
                }
            }
            $cont++;
            $position++;
        }

        return $result;
    }

    public function downloadHistoryClocks(Request $request)
    {
        $filters = $request->all();
        $historyClocks = $this->getHistoryClocks($filters);

        $base64Image = "data:image/png;base64,";

        $cabecera = '
            <table style="width: 100%; margin-bottom: 8px;">
                <tr style="border: none;">
                    <td style="width: 100%;border: none;">
                        <img src="' . $base64Image . '" style="width: 100%;">
                    </td>
                </tr>
            </table>
        ';

        $body = '
            <style>
                table {
                    width: 100%;
                    border-collapse: collapse;
                    font-size: 10px
                }
                th, td {
                    border: 1px solid black;
                    padding: 10px;
                    text-align: center;
                }
                th {
                    background-color: #f2f2f2;
                }
            </style>';

        $body .= "<h3>Historial de fichajes</h3>";

        if (isset($filters['startDate']) && isset($filters['endDate'])) {
            $body .= "<h4>Periodo: {$filters['startDate']} - {$filters['endDate']} </h4>";
        }


        $body .= " 
            <table>
                <tr>
                    <th>Fecha</th>
                    <th>Nombre de usuario</th>
                    <th>Hora de entrada</th>
                    <th>Hora de salida</th>
                    <th>Total de horas</th>
                </tr>";

        foreach ($historyClocks as $user => $date) {
            foreach ($date as $d => $day) {
                foreach ($day['hour'] as $raw) {
                    $total = $raw['TOTAL'] ?? '';
                    $body .= "<tr>
                        <td>{$d}</td>
                        <td>{$user}</td>
                        <td>{$this->trasnformDateToHour($raw['E'] ?? '')}</td>
                        <td>{$this->trasnformDateToHour($raw['S'] ?? '')}</td>
                        <td>{$total}</td>
                      </tr>";
                }
            }
        }
        $body .= '</table>';
        $html = $cabecera . $body;

        // Crear el archivo PDF utilizando la librera Dompdf
        $dompdf = new Dompdf();
        $dompdf->loadHtml($html);
        $dompdf->setPaper('A4', 'portrait');
        $dompdf->render();

        // Descargar el archivo PDF 
        $output = $dompdf->output();

        return response($output, 200)
            ->header('Content-Type', 'application/pdf')
            ->header('Content-Disposition', 'inline; filename="clocking_report.pdf"');
    }

    public function trasnformDateToHour(String $date)
    {
        if (empty($date)) {
            return '';
        }
        $time = strtotime($date);
        $hours = date('H', $time);
        $minutes = date('i', $time);
        $seconds = date('s', $time);

        return $hours . 'h:' . $minutes . 'm:' . $seconds . 's';
    }
}
